@keyframes fadein {
    0% { opacity:0; }
    66% { opacity:0; }
    100% { opacity:1; }
}

body {
	font-size: 20px;
	line-height: 1.5em;
  	font-family: 'PT Sans', sans-serif;
	-webkit-font-smoothing: antialiased;
}

code {
	font-family: Courier, sans-serif !important;
}

p, ul, ol {
	margin-bottom: 1.2em;
	color: #344054;
}

p[style*="font-size:16px"], p[style*="font-size: 16px"] {
	line-height: 24px !important;
}

p[style*="font-size:14px"], p[style*="font-size: 14px"] {
	line-height: 20px !important;
}

p.has-small-font-size {
	line-height: 18px !important;
}

table {
	color: #344054;
}

table a {
	color: #209cad;
}

p:last-child {
	margin-bottom: 0 !important;
}

caps {
	font-size: 0.85em;
	text-transform: uppercase;
}

strong, b {
	font-weight: bold;
}

em, i {
	font-style: italic;
}

a {
	color: inherit;
}

h1, h2, h3, h4, h5, h6 {
	font-weight: bold;
	line-height: 1.2em;
	margin: 0.2em 0 0.5em 0;
	color: #16224E;
   	word-break: keep-all;
    overflow-wrap: normal;
}

h1 { font-size: 60px; }

h2 { font-size: 48px; }

h3 { font-size: 40px; }

h4 { font-size: 32px; }

h5 { font-size: 24px; }

h5 mark {
	display: inline-block;
	width: 40px;
	height: 40px;
	font-size: 20px;
	text-align: center;
	line-height: 40px;
	border-radius: 50%;
	margin-right: 15px;
}

h5 img {
	margin: 0 !important;
	height: 24px !important;
	width: auto !important;
	position: relative;
	top: 6px;
}

h6 { 
	font-size: 20px; 
}


h1 caps, h2 caps, h3 caps, h4 caps {
	//font-family: 'Seravek';
	//font-weight: normal
}

h1 img {
	width: auto !important;
	height: 0.85em !important;
}

h1 sup {
	font-size: 0.6em;
}

sub,
sup {
	font-size: 0.75em;
	line-height: 1.4em;
	vertical-align: bottom;
	font-weight: 100;
}

blockquote sup,
h1 sup {
	vertical-align: top;
}

ol {
	list-style-type: decimal;
	margin-left: 0;
	padding-left: 20px;
}

ol li {
	padding-left: 15px;
}

ol li::marker {
	font-weight: bold;
} 

ul li {
	margin-left: 25px;
}

ul.is-style-arrow li {
	margin-bottom: 1em;
}

ul.is-style-checked li {
	line-height: 28px;
	margin-top: 10px;
}

ul.is-style-checked[style*="font-size:16px"] li {
	line-height: 22px;
}

ul.is-style-checked li::before {
	mask-image: url("data:image/svg+xml;base64,");
	-webkit-mask-image: url("data:image/svg+xml;base64,");
	top: 26px;
	left: -20px;
	color: #fff;
	z-index: 10;
}

ul.is-style-checked li.disabled::before {
	mask-image: url("data:image/png;base64,");
	-webkit-mask-image: url("data:image/png;base64,");
	color: #c4122e !important;
}

ul.is-style-checked li::after {
	content: '';
    display: block;
    position: absolute;
    width: 22px;
    height: 22px;
    border-radius: 50%;
    border: 1px solid transparent;
    background: #209cad;
    left: -26px;
    top: 2px;
}

ul.is-style-checked[style*="font-size:16px"] li::before,
ul.is-style-checked[style*="font-size:16px"] li::after {
	transform: scale(0.95) translateY(-3px) ;
}

div[style*="background-color:#209cad"] ul.is-style-checked li::before,
div.has-tropical-lagoon-500-background-color ul.is-style-checked li::before {
	color: #209cad;
} 

div[style*="background-color:#14727f"] ul.is-style-checked li::before,
div.has-tropical-lagoon-700-background-color ul.is-style-checked li::before {
	color: #14727f;
} 

div[style*="background-color:#209cad"] ul.is-style-checked li::after,
div.has-tropical-lagoon-500-background-color ul.is-style-checked li::after,
div[style*="background-color:#14727f"] ul.is-style-checked li::after,
div.has-tropical-lagoon-500-background-color ul.is-style-checked li::after  {
	border-color: #fff;
	background: #fff;
}

ul.is-style-checked.checks-only li::before,
.card ul.is-style-checked li:not(.circled)::before {
	color: #209cad;
}

.card ul.is-style-checked li::before {
	top: 22px;
}

ul.is-style-checked.checks-only li::after,
.card ul.is-style-checked li:not(.circled)::after {
	display: none;
}

.card ul.is-style-checked li::after {
	top: -2px;
}

blockquote {
	font-size: 24px;
	line-height: 32px;
}

cite {
	display: inline-block;
	font-size: 20px;
	line-height: 24px;
	font-style: normal;
}

cite sub {
	font-size: 85%;
	vertical-align: top;
	opacity: 0.8;
}

#contact .stamps figure {
	max-width: 140px;
	max-height: 100px;
	padding-right: 20px;
}

#contact .stamps figure img {
	object-fit: contain !important;
	object-position: center left;
}

